        // Model\ub4e4\uc740 Rx\ub97c \ud1b5\ud574 Presenter\uc5d0\uac8c \uc790\uc2e0\uc758 \ubcc0\ud654\ub97c \uc54c\ub9ac\uace0, Presenter\uc740 Viw\ub97c \uc5c5\ub370\uc774\ud2b8 \ud55c\ub2e4.\n        enemy.CurrentHp.SubscribeToText(MyText);\n        enemy.IsDead.Where(isDead => isDead == true)\n            .Subscribe(_ =>\n            {\n                MyToggle.interactable = MyButton.interactable = false;\n            });\n    }\n}\n\n// Model. \ubaa8\ub4e0 \ud504\ub85c\ud37c\ud2f0\ub294 \uac12\uc758 \ubcc0\uacbd\uc744 \uc54c\ub824 \uc900\ub2e4. (ReactiveProperty)\npublic class Enemy\n{\n    public ReactiveProperty<long> CurrentHp { get; private set; }\n\n    public ReactiveProperty<bool> IsDead { get; private set; }\n\n    public Enemy(int initialHp)\n    {\n        // \ud504\ub85c\ud37c\ud2f0 \uc815\uc758\n        CurrentHp = new ReactiveProperty<long>(initialHp);\n        IsDead = CurrentHp.Select(x => x <= 0).ToReactiveProperty();\n    }\n}\n")),(0,l.kt)("p",null,"View\ub294 \ud558\ub098\uc758 Scene\uc774\uba70, Unity\uc758 hierarchy\uc774\ub2e4. (\ud558\ub098\uc758 \uac1c\uccb4 \ud639\uc740 \uac1d\uccb4?)"),(0,l.kt)("p",null,"View\ub294 \ucd08\uae30\ud654\uc2dc Unity \uc5d4\uc9c4\uc5d0 \uc758\ud574 Presenter\uc640 \uc5f0\uacb0\ub41c\ub2e4."),(0,l.kt)("p",null,"XxxAsObservable \uba54\uc11c\ub4dc\ub97c \uc0ac\uc6a9\ud558\uba74 \uc624\ubc84 \ud5e4\ub4dc\uc5c6\uc774 \uc774\ubca4\ud2b8 \uc2e0\ud638\ub97c \uac04\ub2e8\ud558\uac8c \uc0dd\uc131 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. SubscribeToText \ubc0f SubscribeToInteractable\uc740 \uac04\ub2e8\ud55c \ubc14\uc778\ub529 \ucc98\ub7fc \uc0ac\uc6a9\ud560 \uc218 \uc788\uac8c \ud558\ub294 helper \ud074\ub798\uc2a4 \uc785\ub2c8\ub2e4. \uc774\uac83\uc740 \ub2e8\uc21c\ud55c \ub3c4\uad6c \uc77c \uc218 \uc788\uc9c0\ub9cc \ub9e4\uc6b0 \uac15\ub825\ud569\ub2c8\ub2e4. Unity \ud658\uacbd\uc5d0\uc11c \uc790\uc5f0\uc2a4\ub7fd\uac8c \ub290\uaef4\uc9c0\uba70 \uace0\uc131\ub2a5\uacfc \uae68\ub057\ud55c \uc544\ud0a4\ud14d\ucc98\ub97c \uc81c\uacf5\ud569\ub2c8\ub2e4."),(0,l.kt)("p",null,(0,l.kt)("img",{alt:"MV(R)P",src:n(91640).Z,width:"700",height:"305"})),(0,l.kt)("ul",null,(0,l.kt)("li",{parentName:"ul"},(0,l.kt)("p",{parentName:"li"},"V-> RP-> M-> RP-> V\uac00 \uc644\uc804\ud788 Reactive(\ubc18\uc751\uc801\uc778)\ud55c \ubc29\ubc95\uc73c\ub85c \uc5f0\uacb0\ub418\uc5c8\ub2e4.")),(0,l.kt)("li",{parentName:"ul"},(0,l.kt)("p",{parentName:"li"},"GUI \ud504\ub85c\uadf8\ub798\ubc0d\uc740 ObservableTrigger\uc758 \uc774\uc810\ub3c4 \uc81c\uacf5\ud569\ub2c8\ub2e4. ObservableTrigger\ub294 Unity \uc774\ubca4\ud2b8\ub97c Observable\ub85c \ubcc0\ud658\ud558\ubbc0\ub85c\uc774\ub97c \uc0ac\uc6a9\ud558\uc5ec MV(R)P \ud328\ud134\uc744 \uad6c\uc131 \ud560 \uc218 \uc788\uc2b5\ub2c8\ub2e4. \uc608\ub97c \ub4e4\uc5b4 ObservableEventTrigger\ub294 uGUI \uc774\ubca4\ud2b8\ub97c Observable\ub85c \ubcc0\ud658\ud569\ub2c8\ub2e4."))),(0,l.kt)("pre",null,(0,l.kt)("code",{parentName:"pre",className:"language-csharp"},"var eventTrigger = this.gameObject.AddComponent<ObservableEventTrigger>();\neventTrigger.OnBeginDragAsObservable()\n    .SelectMany(_ => eventTrigger.OnDragAsObservable(), (start, current) => UniRx.Tuple.Create(start, current))\n    .TakeUntil(eventTrigger.OnEndDragAsObservable())\n    .RepeatUntilDestroy(this)\n    .Subscribe(x => Debug.Log(x));\n")),(0,l.kt)("h2",{id:"\uc124\uacc4-\ubc29\ud5a5"},"\uc124\uacc4 \ubc29\ud5a5"),(0,l.kt)("ul",null,(0,l.kt)("li",{parentName:"ul"},"MVP \ud328\ud134\uc744 \ubcf4\uba74\uc11c \ud5f7\uac08\ub9ac\uac70\ub098 \uc815\ub9bd\ub418\uc9c0 \uc54a\ub294 \ubd80\ubd84\uc740 \uacfc\uac10\ud788 \ub0b4 \ubc29\uc2dd\uc73c\ub85c \uc815\ub9bd\ud558\uace0, \uad6c\ud604 \ud6c4 \ubb38\uc81c\uc810 \ubc1c\uc0dd\uc2dc \uac1c\uc120\ud558\ub294 \ubc29\ud5a5\uc73c\ub85c \uc9c4\ud589."),(0,l.kt)("li",{parentName:"ul"},"\uc644\uc804\ud788 \ub514\uc790\uc778 \ud328\ud134\uc744 \ub530\ub974\uc9c0\ub294 \uc54a\uc744 \uc608\uc815 (\ud074\ub9b0 \ucf54\ub4dc\uac00 \ub418\ub294 \ub300\uc2e0 \uc0dd\